import React, { useEffect, useState } from "react";
import Axios from "axios";
import { API_KEY } from "../App";
import styled from "styled-components";

const Container = styled.div`
  display: flex;
  flex-direction: row;
  padding: 20px 30px;
  justify-content: center;
  border-bottom: 1px solid lightgray;
`;
const CoverImage = styled.img`
  object-fit: cover;
  height: 350px;
`;
const InfoColumn = styled.div`
  display: flex;
  flex-direction: column;
  margin: 20px;
`;
const MovieName = styled.span`
  font-size: 22px;
  font-weight: 600;
  color: black;
  margin: 15px 0;
  white-space: nowrap;
  overflow: hidden;
  text-transform: capitalize;
  text-overflow: ellipsis;
  & span {
    opacity: 0.8;
  }
`;
const MovieInfo = styled.span`
  font-size: 16px;
  font-weight: 500;
  color: black;
  overflow: hidden;
  margin: 4px 0;
  text-transform: capitalize;
  text-overflow: ellipsis;

  & span {
    opacity: 0.5;
  }
`;
const Close = styled.span`
  font-size: 16px;
  font-weight: 600;
  color: black;
  background: lightgray;
  height: fit-content;
  padding: 8px;
  border-radius: 50%;
  cursor: pointer;
  opacity: 0.8;
`;
const MovieInfoComponent = (props) => {
  const [movieInfo, setMovieInfo] = useState();
  const { selectedMovie } = props;

  useEffect(() => {
    Axios.get(
      `https://api.themoviedb.org/3/movie/${selectedMovie}?api_key=${API_KEY}`,
    ).then((response) => setMovieInfo(response.data));
  }, [selectedMovie]);
  return (
    <Container>
      {movieInfo ? (
        <>
          <CoverImage src={`http://image.tmdb.org/t/p/w185/${movieInfo?.poster_path}`} alt={movieInfo?.title} />
          <InfoColumn>
            <MovieName>
              <span>{movieInfo?.title}</span>
            </MovieName>
            <MovieInfo>
              Rating: <span>{movieInfo?.vote_average}</span>
            </MovieInfo>
            <MovieInfo>
              Release Date: <span>{movieInfo?.release_date}</span>
            </MovieInfo>
            <MovieInfo>
              Language: <span>{movieInfo?.original_language}</span>
            </MovieInfo>
            <MovieInfo>
              Runtime: <span>{movieInfo?.runtime} Minutes</span>
            </MovieInfo>
            <MovieInfo>
              Genre: <span>{movieInfo?.genres[0]?.name}</span>
            </MovieInfo>
            <MovieInfo>
              Plot: <span>{movieInfo?.overview}</span>
            </MovieInfo>
          </InfoColumn>
          <Close onClick={() => props.onMovieSelect()}>X</Close>
        </>
      ) : (
        "Loading..."
      )}
    </Container>
  );
};
export default MovieInfoComponent;
